import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Background from './images/bg-01.jpg';
import  { Field } from './Field';
import './vendor/bootstrap/css/bootstrap.min.css';
import './fonts/font-awesome-4.7.0/css/font-awesome.min.css';
import './vendor/animate/animate.css';
import './vendor/css-hamburgers/hamburgers.min.css';
import './vendor/select2/select2.min.css';
import './css/util.css';
import './css/main.css';
const sectionStyle = {
    backgroundImage: 'url(' + Background + ')',
};



class ContactForm extends Component {

    constructor(props) {
      super(props);
      this.state = {name: '0',
                    email: "1",
                    message: '',
                    isMailValid : true,
                    isMessageValid : true,
                    isNameValid: true,
                    serverName: '',
                    body: '',
                    response: '',
                    post: '',
                    responseToPost: '',
    };
    }

    componentDidMount() {
      this.callApi()
        .then(res => this.setState({ response: res.express }))
        .catch(err => console.log(err));
    }
    handleUserInput = (e) => {
    const name = e.target.name;
    const value = e.target.value;
    this.setState({[name]: value})
    }

    ValidateUserInput = (e) => {
      const name = e.target.name;
      const value = e.target.value;
      let isValid= false;
      switch(name) {
        case 'email':
          isValid=this.valideMail(value)
          break
        case 'name':
          isValid=this.valideName(value)
          break
        case 'message' : 
          isValid=this.valideMessage(value)
          break

        default:
        break;
      }
        if (!isValid) {      e.target.className  = " error-input100 input100"
      }
      else       e.target.className  = " success-input100 input100"

    }

    valideMail = (mail) => {
      const mailformat = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;
      if( mail.match(mailformat))
      {
          this.setState ({isMailValid : true})
          return true;
      }
      else
      {
        this.setState ({isMailValid : false})
      return false;

      }
    }
    valideMessage = (message) => {
      if (message == ''){
        this.setState ({isMessageValid : false})
          return false;
      }
      else {
        this.setState ({isMessageValid : true})
        return true;
      }
      return true;
    }
    valideName = (name) => {
      return true;
    }


  callApi = async () => {
    const response = await fetch('/api/hello');
    const body = await response.json();

    if (response.status !== 200) throw Error(body.message);

    return body;
  };

  handleSubmit = async e => {
    e.preventDefault();
    const response = await fetch('/api/world', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ post: this.state.email }),
    });
    const body = await response.text();

    this.setState({ responseToPost: body });
  };

   /*  handleSubmitBTN = async e => {
        e.preventDefault();
        const response = await fetch('/api/hello', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ post: 'Hello' }),
        });
        const body = await response.text();
        this.setState({
            respone : body
        })
    }; */
  render() {
    const errosStyles = {
      display : this.state.isMessageValid ? "none" : "block",
     color : 'red',
    }
    return (
      <div className="App">
        <div className="bg-contact100" style={ sectionStyle } >
            <div className="container-contact100">
              <div className="wrap-contact100">
                <div className="contact100-pic js-tilt" data-tilt>
                  <img src={require('./images/img-01.png')}  alt="IMG"/>
                </div>
                <form className="contact100-form validate-form" onSubmit={this.handleSubmit}>
                  <span className="contact100-form-title">
                    Get in touch
                    Body : {this.state.body} || Body 2 : {this.state.serverName}
                  </span>
                  <Field type="text" 
                         name="name" 
                         icon="fa-user" 
                         onchange= {(e) => {this.handleUserInput(e)}}
                         onBlur = {(e) => {this.ValidateUserInput(e)}} 
                         value={this.state.name}
                         isValid = {this.state.isNameValid}/>


                  <Field type="text" 
                         name="email" 
                         icon="fa-envelope" 
                         onchange= {(e) => {this.handleUserInput(e)}} 
                         onBlur = {(e) => {this.ValidateUserInput(e)}} 
                         value={this.state.email}
                         isValid = {this.state.isMailValid} />


                  <Field type="textarea"
                         name="message"
                         onchange= {(e) => {this.handleUserInput(e)}}
                         onBlur = {(e) => {this.ValidateUserInput(e)}}
                         value={this.state.message}
                         isValid = {this.state.isMessageValid}/>

                  <div className="container-contact100-form-btn">
                    <input type="submit" value="send"  className="contact100-form-btn" />
                    <button className="contact100-form-btn"  onClick={this.onSubmit}>

                      Send
                    </button>
                    <p>{this.state.responseToPost}</p>
                  </div>
                </form>
              </div>
            </div>
        </div>

        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
        </header>
        <p>{this.state.response}</p>
        <form onSubmit={this.handleSubmit}>
          <p>
            <strong>Post to Server:</strong>
          </p>
          <input
            type="text"
            value={this.state.post}
            onChange={e => this.setState({ post: e.target.value })}
          />
          <button type="submit">Submit</button>
        </form>
        <p>{this.state.responseToPost}</p>

      </div>
    );
  }
}

export default ContactForm;
